import React, { useState } from "react";

function Sidebar() {
  const [currentMainMenu, setCurrentMainMenu] = useState(0);

  const handleMainMenu = (id) => {
    if (id === currentMainMenu) {
      setCurrentMainMenu(0);
      return;
    }
    setCurrentMainMenu(id);
  };
  return (
    <sidebar className="sidebar">
      <ul>
        <li onClick={() => handleMainMenu(1)}>
          <a href="#">Top link</a>
        </li>
        <li
          className={currentMainMenu == 2 ? "open" : ""}
          onClick={() => handleMainMenu(2)}
        >
          <a href="#">
            Top menu 1<span className="arrow"></span>
          </a>
          {currentMainMenu == 2 && (
            <div className="submenu">
              <ul>
                <li>
                  <a href="#">Top Submenu 1</a>
                </li>
                <li>
                  <a href="#">Top Submenu 2</a>
                </li>
                <li>
                  <a href="#">Top Submenu 3</a>
                </li>
                <li>
                  <a href="#">Top Submenu 4</a>
                </li>
              </ul>
            </div>
          )}
        </li>
        <li onClick={() => handleMainMenu(3)}>
          <a href="#">
            Top menu 2<span className="arrow"></span>
          </a>
        </li>
      </ul>
    </sidebar>
  );
}

export default Sidebar;
